import {takeLatest,put,all ,call} from 'redux-saga/effects'
import UserActionTypes from './user.types'
import {googleprovider,auth,createUserProfileDocument} from '../../firebase/firebase.util'
import {googleSignInSuccess,googleSignInFailure,emailSignInFailure,emailSignInSuccess} from './user.actions';
export function* signInWithGoogle(){
    try{
        const {user}=yield auth.signInWithPopup(googleprovider);
        const userRef=yield call(createUserProfileDocument,user);
        const userSnapshot=yield userRef.get();
        yield put(
            googleSignInSuccess({id:userSnapshot.id,...userSnapshot.data()})
        )

    }catch(error){
       yield put(googleSignInFailure(error)); 
    }
}
export function* onGoogleSignInStart(){
    yield takeLatest(UserActionTypes.GOOGLE_SIGN_IN_START,
    signInWithGoogle    )
}
export function* signInWithEmail({payload:{email,password}}){
try{
const {user} =yield auth.signInWithEmailAndPassword(email,password);
const userRef=yield call(createUserProfileDocument,user);
const userSnapshot=yield userRef.get();
        yield put(
            emailSignInSuccess({id:userSnapshot.id,...userSnapshot.data()})   //update user reducer with the obj we pass in
        )


}
catch(err){
 yield put (emailSignInFailure(err));
}
}
export function* onEmailSignInStart(){
    yield takeLatest(UserActionTypes.EMAIL_SIGN_IN_START,)
}
export function* userSagas(){
    yield all([call(onGoogleSignInStart)]);
}
