import React from "react";
import {Route } from 'react-router-dom'
import CollectionPage from '../collection/collection.component'
import CollectionsOverviewContainer from '../../components/collections-overview/collection-overview.container'
import {connect } from 'react-redux';
import {createStructuredSelector} from 'reselect';
import {fetchCollectionsStartAsync} from '../../redux/shop/shop.actions';
import {selectIsCollectionFetching,selectIsCollectionsLoaded} from '../../redux/shop/shop-selectors';
import withSpinner from '../../components/spinner/spinner.component';
import collectionsOverviewComponent from "../../components/collections-overview/collections-overview.component";
const CollectionOverviewWithSpinner=withSpinner(CollectionsOverview);
const CollectionPageWithSpinner =withSpinner(CollectionPage);

class ShopPage extends React.Component{
    state={
        loading:true
    };

    unsuscribeFromSnapshot=null;
    componentDidMount(){
     const {fetchCollectionsStartAsync}=this.props;
     fetchCollectionsStartAsync();  

    //  collectionRef.onSnapshot( async snapshot=>{                         ///this is observqablle pattern 
    //  const collectionsMap=convertCollectionsSnapShotToMap(snapshot);
    //     updateCollections(collectionsMap);
    //      this.setState({loading:false})});
    }
    render(){
        const {match,isCollectionsLoaded}=this.props;
        return(
            <div className="shop-page">
                <Route exact path={`${match.path}`}component={collectionsOverviewComponent}></Route>
                <Route path={`${match.path}/:collectionId`} render ={(props)=>(<CollectionPageWithSpinner isLoading={!isCollectionsLoaded} {...props}/>)} ></Route>
        </div>);  
    }
}
const mapStateToProps=createStructuredSelector({
    isCollectionsLoaded:selectIsCollectionsLoaded
});
const mapdispatchToProps=(dispatch)=>({
    fetchCollectionsStartAsync:()=>dispatch(fetchCollectionsStartAsync())
})


export default connect(mapStateToProps,mapdispatchToProps)(ShopPage);
